<?phpnamespace app\api\validate;use app\common\model\User;use think\Validate;class ConcreteRatio extends Validate{    protected $rule = [        'id'=> 'require',        'user_id'=> 'require|checkUserExist|checkUserDepart',        'task_id'=> 'require',        'content'=> 'require',    ];    protected $message = [        'id.require' => '混凝土表id不能为空',        'user_id.require' => '用户id不能为空',        'task_id.require' => '任务id不能为空',        'task_id.unique' => '任务id已存在',        'content.require' => '内容不能为空',    ];    protected $scene = [        //查看        'look'=>[            'user_id'=>'require|checkUserExist',            'task_id',        ],        //发布        'add'=>[            'user_id',            'task_id'=>[                'require',                'unique'=>'concrete_ratio',            ],            'content',        ],        //编辑        'edit'=>[            'id',            'user_id',            'content',        ],    ];    //验证用户id是否存在    protected function checkUserExist($value)    {        $user = new User();        $res = $user->where(['id'=>$value])->value('id');        if (!$res)            return '用户id不存在';        return true;    }    //验证用户部门是否正确    protected function checkUserDepart($value)    {        $user = new User();        $depart = $user->where(['id'=>$value])->value('department_id');        if ($depart != 5)            return '用户无此权限';        return true;    }}