<?phpnamespace app\api\validate;use app\common\model\User;use think\Validate;class Message extends Validate{    protected $rule = [        'user_id'=> 'require|checkUserExist',        'task_id'=> 'require',    ];    protected $message = [        'user_id.require' => '用户id不能为空',        'task_id.require' => '任务id不能为空',    ];    protected $scene = [        //获取消息(一级页面)        'get'=>[            'user_id',        ],        //获取消息(二级页面)        'getMessage'=>[            'user_id',            'task_id',        ],    ];    //验证用户id是否存在    protected function checkUserExist($value)    {        $user = new User();        $res = $user->where(['id'=>$value])->value('id');        if (!$res)            return '用户id不存在';        return true;    }}