<?phpnamespace app\api\validate;use app\common\model\User as UserModel;use think\Validate;class Pedestal extends Validate{    protected $rule = [        'user_id'=> 'require|checkUserExist',        'pedestal_id'=> 'require',        'title'=> 'require',    ];    protected $message = [        'user_id.require' => '用户id不能为空',        'pedestal_id.require' => '台座id不能为空',        'title.require' => '台座名称不能为空',    ];    protected $scene = [        //查询资料        'getInformation'=>[            'user_id'        ],    ];    //验证用户id是否存在    protected function checkUserExist($value)    {        $user = new UserModel();        $res = $user->where(['id'=>$value])->value('id');        if (!$res)            return '用户id不存在';        return true;    }}