<?phpnamespace app\api\validate;use app\common\model\User;use app\common\model\TaskFlow;use think\Validate;class ReturnFeedback extends Validate{    protected $rule = [        'user_id'=> 'require|checkUserExist',        'task_id'=> 'require',        'return_id'=> 'require',        'reason'=> 'require',        'feedback_id'=> 'require',    ];    protected $message = [        'user_id.require' => '用户id不能为空',        'task_id.require' => '任务id不能为空',        'return_id.require' => '返工表id不能为空',        'reason.require' => '反馈内容不能为空',        'feedback_id.require' => '反馈表id不能为空',    ];    protected $scene = [        //查询记录        'add'=>[            'user_id',            'task_id',            'return_id',            'reason',        ],        //查询记录        'detail'=>[            'user_id',            'feedback_id',        ],    ];    //验证用户id是否存在    protected function checkUserExist($value)    {        $user = new User();        $res = $user->where(['id'=>$value])->value('id');        if (!$res)            return '用户id不存在';        return true;    }}