<?phpnamespace app\api\validate;use app\common\model\User;use app\common\model\TaskFlow;use think\Validate;class ReturnTask extends Validate{    protected $rule = [        'user_id'=> 'require|checkUserExist',        'return_id'=> 'require',    ];    protected $message = [        'user_id.require' => '用户id不能为空',        'return_id.require' => '返工表id不能为空',    ];    protected $scene = [        //查询记录        'get'=>[            'user_id',        ],        //查询记录        'detail'=>[            'user_id',            'return_id',        ],        //删除记录        'del'=>[            'user_id',            'return_id',        ],    ];    //验证用户id是否存在    protected function checkUserExist($value)    {        $user = new User();        $res = $user->where(['id'=>$value])->value('id');        if (!$res)            return '用户id不存在';        return true;    }}