<?phpnamespace app\api\validate;use app\common\model\User;use app\common\model\TaskFlow;use think\Validate;class Stripping extends Validate{    protected $rule = [        'id'=> 'require',        'user_id'=> 'require|checkUserExist|checkUserDepart',        'task_id'=> 'require',        'title'=> 'require',        'priming_time'=> 'require',        'serial_number'=> 'require',        'pedestal_number'=> 'require',        'bridge_number'=> 'require',        'core_temperature'=> 'require',        'surface_temperature'=> 'require',        'environment_temperature'=> 'require',        'compressive_strength'=> 'require',        'conclusion'=> 'require',    ];    protected $message = [        'id.require' => '拆模通知单id不能为空',        'user_id.require' => '用户id不能为空',        'task_id.require' => '任务id不能为空',        'task_id.unique' => '任务id已存在',        'title.require' => '标题不能为空',        'priming_time.require' => '灌注时间不能为空',        'serial_number.require' => '编号不能为空',        'pedestal_number.require' => '台座号不能为空',        'bridge_number.require' => '梁号不能为空',        'core_temperature.require' => '芯部温度不能为空',        'surface_temperature.require' => '表层温度不能为空',        'environment_temperature.require' => '环境温度不能为空',        'compressive_strength.require' => '抗压强度不能为空',        'conclusion.require' => '结论不能为空',    ];    protected $scene = [        //发布        'add'=>[            'user_id',            'task_id'=>[                'require',                'unique'=>'stripping',            ],            'title',            'priming_time',            //'serial_number',            'pedestal_number',            'bridge_number',            'core_temperature',            'surface_temperature',            'environment_temperature',            'compressive_strength',            'conclusion',        ],        //获取参数        'get'=>[            'user_id',            'task_id',        ],        //获取参数        'look'=>[            'task_id',        ],        //修改        'edit'=>[            'id',            'user_id',            'task_id'=>[                'require',                'unique'=>'stripping',            ],            'title',            'priming_time',            //'serial_number',            'pedestal_number',            'bridge_number',            'core_temperature',            'surface_temperature',            'environment_temperature',            'compressive_strength',            'conclusion',        ],    ];    //验证用户id是否存在    protected function checkUserExist($value)    {        $user = new User();        $res = $user->where(['id'=>$value])->value('id');        if (!$res)            return '用户id不存在';        return true;    }    //验证用户部门是否正确    protected function checkUserDepart($value)    {        $user = new User();        $depart = $user->where(['id'=>$value])->value('department_id');        if ($depart != 5)            return '用户无此权限';        return true;    }}