<?phpnamespace app\common\model;use think\Db;use think\Exception;use think\Session;//混凝土配比class ConcreteRatio extends BaseModel{    //添加配比    function addRatio($data)    {        $this->allowField(true)->save($data);    }    //编辑配比    function editRatio($data)    {        $this->allowField(true)->isUpdate(true)->save($data);    }    //查看配比    function lookRatio($task_id)    {        $data = $this ->where('task_id',$task_id)            ->field([                'id',                'task_id',                'user_id',                'content',                'create_time',            ])            ->find();        return $data;    }}