<?phpnamespace app\common\model;use think\Exception;use think\Session;//部门class Department extends BaseModel{    //获取部门列表(用于后台)    function getConsoleList($where)    {        $list = $this->where($where)            ->paginate(20, false, ['query' => request()->get()]);        return $list;    }    //添加部门(用于后台)    function add($data)    {        $this->allowField(true)->save($data);    }    //编辑部门(用于后台)    function edit($data)    {        $this->allowField(true)->isUpdate(true)->save($data);    }}