<?phpnamespace app\common\model;use think\Db;use think\Exception;use app\common\model\Process;use app\common\model\TaskProcess;use think\Session;//绑扎胎具使用记录class MouldLog extends BaseModel{    function getList($where,$order)    {        $list = $this->alias('l')            ->join('mould m','m.id=l.mould_id','left')            ->join('task t','t.id=l.task_id','left')            ->field([                'l.id',                'l.mould_id',                'm.title as mould_title',//胎具名称                'l.task_id',                't.title as task_title',//任务名称                'l.create_time',                'l.finish_time',            ])            ->where($where)            ->order($order)            //->order('create_time desc')            ->paginate(20, false, ['query' => request()->get()]);        return $list;    }    //添加胎具使用记录    function add($data)    {        return $this->allowField(true)->save($data);    }}