<?phpnamespace app\common\model;use think\Db;use think\Exception;use app\common\model\Process;use app\common\model\TaskProcess;use think\Session;//台座使用记录class PedestalLog extends BaseModel{    function getList($where,$order)    {        $list = $this->alias('l')            ->join('pedestal p','p.id=l.pedestal_id','left')            ->join('task t','t.id=l.task_id','left')            ->field([                'l.id',                'l.pedestal_id',                'p.title as pedestal_title',//胎具名称                'l.task_id',                't.title as task_title',//任务名称                'l.create_time',                'l.finish_time',            ])            ->where($where)            ->order($order)            //->order('create_time desc')            ->paginate(20, false, ['query' => request()->get()]);        return $list;    }    //添加台座使用记录    function add($data)    {        return $this->allowField(true)->save($data);    }}