<?phpnamespace app\common\model;use think\Exception;use think\Session;//工序class Process extends BaseModel{    //获取工序列表(用于后台)    function getConsoleList($where)    {        $list = $this->where($where)            ->paginate(20, false, ['query' => request()->get()]);        return $list;    }    //添加工序(用于后台)    function addProcess($data)    {        $this->allowField(true)->save($data);    }    //编辑工序(用于后台)    function edit($data)    {        $this->allowField(true)->isUpdate(true)->save($data);    }    //按条件获取工序列表    function getProcess($where,$page=1)    {        $list= $this->where($where)            ->field([            'id',            'title',            'duration',        ])->paginate(100, false, ['page' =>$page]);        return $list;    }}